using UnityEngine;using UnityEngine.Networking;using System.Collections;public class Bullet : NetworkBehaviour {    [HideInInspector]    public PlayerMovement player;    public int damageGiven = 1;    void Start()    {        StartCoroutine(DestroyLater());    }    IEnumerator DestroyLater()    {        yield return new WaitForSeconds(2.0f);        player.numBullets--;        Destroy(gameObject);    }    public override void OnNetworkDestroy()    {        Destroy(gameObject);    }    [ServerCallback]    void OnTriggerEnter2D(Collider2D collider)    {        if (!isServer)        {            return;        }        var hit = collider.gameObject;        if (hit.tag == "Player")        {            // TODO: for bouncy bullets, allow players to hit themselves            if (hit == player)            {                return;            }            var combat = hit.GetComponent<Combat>();            combat.TakeDamage(damageGiven);            player.numBullets--;            NetworkServer.Destroy(gameObject);        }        else if (hit.tag == "Wall")        {            player.numBullets--;            NetworkServer.Destroy(gameObject);        }        else if (hit.tag == "Bullet")        {            player.numBullets--;            NetworkServer.Destroy(gameObject);        }        else if (hit.name == "Pod")        {            player.numBullets--;            hit.GetComponent<Pod>().TakeDamage(damageGiven);            NetworkServer.Destroy(gameObject);        }    }}