using UnityEngine;using UnityEngine.Networking;using System.Collections.Generic;public class MyLobbyManager : NetworkLobbyManager {    // for users to apply settings from their lobby player object to their in-game player object    public override bool OnLobbyServerSceneLoadedForPlayer(GameObject lobbyPlayer, GameObject gamePlayer)    {        Debug.LogError("OnLobbyServerSceneLoadedForPlayer " + gamePlayer.GetInstanceID());        return true;    }    public override GameObject OnLobbyServerCreateGamePlayer(NetworkConnection conn, short playerControllerId)    {        Debug.LogError("OnLobbyServerCreateGamePlayer " + playerControllerId);        return null; // use default    }}