using UnityEngine;using UnityEngine.Networking;using System.Collections.Generic;public class MyLobbyManager : NetworkLobbyManager {    IEnumerator<Vector3> spawnPoints;    int rows = 16;    int columns = 16;    public override void OnLobbyServerPlayersReady()    {        // TODO: register transforms here for players start positions?    }    // for users to apply settings from their lobby player object to their in-game player object    public override bool OnLobbyServerSceneLoadedForPlayer(GameObject lobbyPlayer, GameObject gamePlayer)    {        if (spawnPoints == null) {            spawnPoints = SpawnPointEnumerable().GetEnumerator();            spawnPoints.MoveNext();        }        gamePlayer.transform.position = GetNextSpawnPoint();        return true;    }    public Vector3 GetNextSpawnPoint()    {        var t = spawnPoints.Current;        spawnPoints.MoveNext();        return t;    }    IEnumerable<Vector3> SpawnPointEnumerable()    {        while (true)        {            yield return new Vector3(0, rows - 1, 0f);            yield return new Vector3(columns - 1, 0, 0f);            yield return new Vector3(columns - 1, rows - 1, 0f);            yield return new Vector3(0, 0, 0f);        }    }}